          },
          callback(data) {
            const self = this;
            // organize data and draw chart
            this.rmse_exp = data[0]; // by model typ, e.g. ANN, RNN, RGCN, RGCN_ptrn
            this.rmse_ann = data[1]; // by model type x experiment with only data from d100 experiment, same variable names
