                 .style("font-size", "30px")
                 .style("font-weight","bold")
                 .style("opacity", this.o_train)
                 .classed("rmse-title", true)

                var legend_r = legend_rmse.selectAll(".legend-rmse")
                  .data(rmse_fill.domain().slice())
                  .enter().append("g")
