          },
          transitionAxes(element, end) {
            const self = this;
            let time_slide = 500;
            element
              .transition()
              .duration(time_slide)
              .ease(this.d3.easeCircle)
              .style("stroke-dashoffset", end)

          },
          moveLegend(direction) {
              const self = this;
            var drop_dot = this.d3.select("g.legend:nth-child(2)")

            var nudge_x = 150;
            var nudge_y = this.height*.1;

            if (direction == "down"){
                  this.d3.select("g.legend:nth-child(2) circle")
                  .transition()
                  .duration(150)
                  .style("opacity", 1)

                  this.d3.select("g.legend:nth-child(2)")
                  .transition()
                  .duration(400)
                  .attr("transform", "translate(" + (nudge_x) + " ,"  + (nudge_y + 60) + ")")

                  this.d3.select("g.legend:nth-child(2) text")
                  .transition()
                  .duration(150)
                  .style("opacity", 1)


            } else if (direction == "up") {
             this.d3.select("g.legend:nth-child(2) text")
                  .transition()
                  .duration(150)
                  .style("opacity", 0)

                this.d3.select("g.legend:nth-child(2)")
                  .transition()
                  .duration(400)
                  .attr("transform", "translate(" + (nudge_x) + " ,"  + (nudge_y + 30) + ")")

                  this.d3.select("g.legend:nth-child(2) circle")
                  .transition()
                  .duration(150)
                  .style("opacity", 0)

            }
          },
          drawAxes(axes_in) {
            const self = this;
            // controls axis aniamtions between error chart and beeswarm
            // to do: clean up and reuse moveXAxis and transitionAxes functions to simplify code here
            let time_slide = 500;
            let margin = 50;

            if (axes_in === "error") {
             this.yAxis
              .transition()
              .duration(time_slide)
              .ease(this.d3.easeCircle)
              .style("stroke-dashoffset", 0)

            this.xAxis
              .transition()
              .duration(time_slide)
              .ease(this.d3.easeCircle)
              .style("stroke-dashoffset", 0)

            } else if (axes_in === "error_up") {
            this.yAxis
              .transition()
              .duration(time_slide)
              .ease(this.d3.easeCircle)
              .style("stroke-dashoffset", this.height+margin)

            this.xAxis
              .transition()
              .duration(time_slide)
              .ease(this.d3.easeCircle)
              .style("stroke-dashoffset", this.width+margin)

            } else if (axes_in === "rmse"){
              // move x-axis up to center line
              this.xAxis
                .transition()
                .duration(time_slide)
                .ease(this.d3.easeCircle)
                .attr("transform", "translate(" + -margin + "," + (this.height/2-50) + ")")

                this.yAxis
                .transition()
                .duration(time_slide)
                .style("opacity", 0)

            } else if (axes_in === "rmse_up"){
              // move x-axis down to bottom

              this.xAxis
                .transition()
                .duration(time_slide)
                .ease(this.d3.easeCircle)
                .attr("transform", "translate(" + -margin + "," + this.height + ")")

                this.yAxis
                .transition()
                .duration(time_slide)
                .style("opacity", 1)
            } 
          },
          updateChart() {
            //controls decision making for the error >> beeswarm chart
            const self = this;

          // where are we?
          //console.log(this.chartState.var_x);
         // console.log(this.chartState.var_y);
          let margin = 50;

          // update axes based on active data
          this.xScale = this.d3.scaleLinear()
            .range([margin, this.width+margin])
            .domain([0,this.chartState.domain_x]);

          this.yScale = this.d3.scaleLinear()
            .range([this.height, margin])
            .domain([0,this.chartState.domain_y]);
            // this totally works but hardly see movment vs scaling??

            // update label under beesawrm with current model data in view
            this.d3.selectAll("text.rmse-name.model")
              .transition()
              .duration(500)
              .style("opacity", 0)
              .remove()

            // redraw label
            this.d3.select("g.legend_color.error")
              .append("text")
              .classed("rmse-name" , true)
              .classed("model" , true)
              .text(this.model_current) // pulls current step model
              .attr("x", 510)
              .attr("y", (this.height*.1)+420)
              .style("fill", "white")
              .style("font-size", "30px")
              .attr("line-height", "30px")
              .style("font-weight","100")
              .style("opacity", 0)
              .transition()
                .duration(200)
                .style("opacity", 1)

        // bind data
          let chart = this.svg.selectAll(".bees") // puts out error on intial draw until scrolled
          .data(this.chartState.dataset, function(d) { return d.seg }) // use seg as a key to bind and update data
          .attr("fill", (d) => self.set_colors(d[this.chartState.grouped])) // transitions color
                .attr("stroke", (d) => self.stroke_colors(d[this.chartState.grouped]))

        // modify forces to update chart
        self.simulation = this.d3.forceSimulation(self.chartState.dataset, function(d) { return d.seg }) // is the key needed here?
          .force("x", this.d3.forceX((d) => self.xScale(d[this.chartState.var_x])).strength(this.chartState.strengthx))
          .force('y', this.d3.forceY((d) => self.yScale(d[this.chartState.var_y])).strength(this.chartState.strengthy))
          .force("collide", this.d3.forceCollide(this.chartState.radius).strength(this.chartState.strengthr).iterations(1))
          .stop();

        // define how elements are added and remove from view
        // attributes and positioning define the starting point
          chart.exit()
              .transition()
                .duration(600)
                .delay(function(d,i) { return 5* i})
                .attr("r", 0)
                //.attr("r", 0)
                //.attr("cx", this.width/2) //where they exit from
                //.attr("cy", (this.height /2)) //where they exit from
                .remove();
          chart
            .transition()
            .duration(800)
            .attr("r", this.radius)
            .attr("fill", (d) => self.set_colors(d[this.chartState.grouped])) // define entering color before appears
            .attr("stroke", (d) => self.stroke_colors(d[this.chartState.grouped]))

            chart.enter()
              .append("circle")
              .classed("bees", true)
              //.attr("cx", this.width/2) // where they enter from
              //.attr("cy", (this.height/2))// where they enter from
              .attr("cx", (d) => self.xScale(d[this.chartState.var_x]))
              .attr("fill", (d) => self.set_colors(d[this.chartState.grouped])) // define entering color before appears
              .attr("stroke", (d) => self.stroke_colors(d[this.chartState.grouped]))
              .attr("r", 0) 
              .transition()
                .duration(800)
                .delay(function(d,i) { return 5* i})
                .attr("r", this.radius)
                //.attr("cx", (d) => self.xScale(d[this.chartState.var_x])) // this made them fly across the screen before fully appearing?
                //.attr("cy", (d) => self.xScale(d[this.chartState.var_y]))

          // anything that should happen after points are updated
            chart
              .merge(chart)
              .transition()
                .duration(800)
                .delay(function(d,i) { return 5* i})
                .attr("fill", (d) => self.set_colors(d[this.chartState.grouped])) // transitions color
                .attr("stroke", (d) => self.stroke_colors(d[this.chartState.grouped]))
                //.attr("cx", (d) => self.xScale(d[this.chartState.measure]))// where they move to
                //.attr("cy", (this.height /2 ) - this.margin/2);// where they move to

          ///////////
          // define force velocity and ticking

         // array of transition steps
          var step_transitions = [70, this.now_step, this.step_error_exp, this.step_error_obs, this.step_rmse,this.step_rmse+1,this.step_rmse+2, this.step_ann, this.step_ann_exp, this.step_rnn, this.step_rgcn, this.step_rgcn_ptrn];

          // tick simulation only if the active step has a chart transition
          if (step_transitions.indexOf(this.step) !== -1){
            
           self.simulation
           .alpha(this.chartState.alpha)
           .alphaDecay(this.chartState.aDecay)
           //.velocityDecay(0.6)
           .restart()
            .on("tick", self.tick)
            // high velocity decay with low alpha decay so it cools more slowly
          }
          },
          tick() {
            // ticking the simulation moves the dots. currently this is run each step
            // needs to be modified to only run if the beeswarm data changes
          const self = this;
          
          this.d3.selectAll(".bees")
            .attr('cx', function(d){return d.x})
            .attr('cy', function(d){return d.y})
        }, 
        // scrollama event handler functions
        // add class on enter, update charts based on step
        handleStepEnter(response) {
          const self = this;
          this.now_step = this.last_step+1;

          // update step variable to match step in view
          this.step = response.index;
          console.log(response);

          ///////////
          // assign force
          // to do: implement with switch to make faster and set forces to be different on intial draw and update
          // the biggest problem with force right now if that if the page is refreshed in the middle of the chart sequence
          // it doesn't have enough heat to make it to the mid line because alpha is down to reduce jitter
          // also work could be done to slow down the dots and exaggerate the transitions more, while still making sure they get to their home spots

          // error chart
          if (this.step <= this.step_error_exp) {
            this.chartState.strengthy = 1;
            this.chartState.radius = 0;
             this.chartState.alpha = 1;
             this.chartState.aDecay = 0.05;
          }
           if (this.step === this.step_error_obs ) {
            this.chartState.radius = 0;
             this.chartState.alpha = 1;
             this.chartState.aDecay = 0.1;
          }
          // push to overlap as single RMSE
          if (this.step === this.step_rmse) {
            this.chartState.strengthy = 1;
            this.chartState.radius = 0;
             this.chartState.strengthr = 1;
             this.chartState.alpha = 1;
             this.chartState.aDecay = 0.05;
          }
          if (this.step === this.step_rmse+1) {
            this.chartState.strengthy = 1;
            this.chartState.radius = 0;
             this.chartState.strengthr = 1;
             this.chartState.alpha = .3;
             this.chartState.aDecay = 0.05;

          }
          if (this.step === this.step_rmse+2) {
            this.chartState.strengthy = 1;
            this.chartState.radius = 0;
             this.chartState.strengthr = 1;
             this.chartState.alpha = .3;
             this.chartState.aDecay = 0.05;
          }

          // intro beeswarm, adding experiments
          // decrease alpha to reduce jitteriness
          // corresponding decrease in alphaDecay to allow "cool down" 
          if (this.step <= this.step_ann_exp && this.step >= this.step_ann) {
            this.chartState.strengthy = 0.9;
            this.chartState.radius = this.paddedRadius;
            this.chartState.alpha = 0.3;
            this.chartState.aDecay = 0.05;
          }
          // RNN
          if (this.step >= this.step_rnn && this.step < this.step_rgcn) {
            this.chartState.strengthy = 0.2;
            this.chartState.radius = this.paddedRadius;
            this.chartState.alpha = 0.2;
            this.chartState.aDecay = 0.15;
          }
          // RGCN
          if (this.step >= this.step_rgcn && this.step <= this.step_rgcn_ptrn) {
            this.chartState.strengthy = 0.2;
            this.chartState.radius = this.paddedRadius;
            this.chartState.alpha = 0.2;
            this.chartState.aDecay = 0.15;
          }
          // RGCN to end
          if (this.step >= this.step_rgcn_ptrn) {
            this.chartState.strengthy = 0.2;
            this.chartState.radius = this.paddedRadius;
            this.chartState.alpha = 0.2;
            this.chartState.aDecay = 0.15;
          }

          /////////// REDRAW
          // now redraw beeswarm chart and modify force based on active data
          // only redraw if the data or forces change
          //this.chartState.strengthy = .2;
          this.chartState.strengthx = 1;
          this.setDataVars(); /// refresh data chart is based on

          if (this.step >= this.step_start ) {
            self.updateChart();
          }

          ///////////
          // toggle intro header to stepped headers
          // this is necessary because the first view is not in the same sticky scolling structure as the rest

          if (this.step == 0 && response.direction == "down") {

            // determine initial model id and initial annotation id
            // NOTE currently assumes that we are beginning the visuals at step 0
            let initial_model_id = self.flubber_id_order[this.step]
            let initial_annotation_id = initial_model_id + "_annotations"

            // display flubber visual associated with initial model id
            self.d3.selectAll("#" + initial_model_id)
              .style("opacity", 1)
              .attr("opacity", 1)

            // display flubber annotation associated with initial model id
            self.d3.selectAll("#" + initial_annotation_id)
              .transition()
              .duration(900)
              .style("opacity", 1)
              .attr("opacity", 1)
            // self.fadeIn(this.d3.selectAll("#flubber-svg"), 900);
          }
        //  // drop sticky header
        //   if (this.step >= 0 && response.direction == "down"){
        //      this.d3.select("figure.intro").classed("sticky", false); 
        //   }

        // updates to go with downscroll
          if (response.direction == "down"){
            if (this.step == this.step_error_exp) {
                self.drawAxes("error"); // draw axes
                self.fadeIn(this.axis_label, this.time_fade); // show error axis labels
                self.fadeIn(this.legend_predicted, this.time_fade); // show predicted in legend

              }  else if (this.step == this.step_error_obs) {
                self.moveLegend("down"); // add observed to legend
              }  else if (this.step == this.step_rmse) {
                self.drawAxes("rmse"); // move legend up to center plot for RMSEs
                // rmse legend in and error legend out
                self.fadeOut(this.axis_label, this.time_fade); // remove error axis labels
                self.fadeOut(this.legend_predicted, this.time_fade); // remove error legend
                self.fadeOut(this.legend_observed, this.time_fade); // remove error legend
                //arrow appears for rmse axis labelling
                self.fadeIn(this.axis_arrow);
                self.fadeIn(this.axis_label_rmse, this.time_fade);// add rmse accurate to inaccurate labels
                self.fadeIn(this.legend_model, this.time_fade); // show rmse model type
              } else if (this.step == this.step_ann) {
                // half of rmse legend appears for beeswarm
                self.fadeIn(this.legend_training, this.time_fade) 
                self.fadeIn(this.legend_training_d100, this.time_fade)

              } else if (this.step == this.step_ann_exp) {
                self.fadeIn(this.legend_training_d001, this.time_fade) 
              } 
          }

           // add class to active step
          response.element.classList.add("is-active");

          // trigger flubber
          this.animateFlubber(response.element.id, response.direction);

        },
        
        handleStepExit(response) {
          const self = this;
          // changes css for class
          response.element.classList.remove("is-active");// add remove class on exit

        // // make intro header sticky again if scrolling back
        //   if (this.step <= 2 && response.direction == "up"){
        //      this.d3.select("figure.intro").classed("sticky", false); 
        //   }


        // updates to go with upscroll
        if (this.step == 0 && response.direction == "up") {

            /// determine initial model id and initial annotation id
            // NOTE currently assumes that we are beginning the visuals at step 0
            let initial_model_id = self.flubber_id_order[this.step]
            let initial_annotation_id = initial_model_id + "_annotations"

            // fade flubber visual associated with initial model id
            self.d3.selectAll("#" + initial_model_id)
              .transition()
              .duration(500)
              .style("opacity", 0)

            // fade flubber annotation associated with initial model id
            self.d3.selectAll("#" + initial_annotation_id)
              .transition()
              .duration(500)
              .style("opacity", 0)
          }

        if (response.direction == "up") {

          if (this.step == this.step_error_exp) {
            self.drawAxes("error_up");
            this.d3.selectAll(".bees").remove()
            self.fadeOut(this.axis_label, this.time_fade);
            self.fadeOut(this.legend_predicted, this.time_fade);
          }  else if (this.step == this.step_error_obs ) {
            self.moveLegend("up");
           self.fadeOut(this.legend_observed, this.time_fade);
          } else if (this.step == this.step_rmse) {
            self.drawAxes("rmse_up");
            self.fadeIn(this.axis_label, this.time_fade);
            self.fadeIn(this.legend_predicted, this.time_fade);
            self.fadeIn(this.legend_observed, this.time_fade);
            self.fadeOut(this.axis_label_rmse, this.time_fade);
            self.fadeOut(this.axis_arrow, this.time_fade);
            self.fadeOut(this.legend_model, this.time_fade);

          } else if (this.step == this.step_ann) {
            // half of legend for beeswarm
             self.fadeOut(this.legend_training, this.time_fade) 
             self.fadeOut(this.legend_training_d100, this.time_fade)
          } else if (this.step == this.step_ann_exp) {
             self.fadeOut(this.legend_training_d001, this.time_fade) 
          } 
        }
        },
        
        fadeOut(element, time) {
          element
          .transition()
          .duration(time)
          .style("opacity",0)
        },
         fadeIn(element, time) {
          element
          .transition()
          .duration(time)
          .style("opacity",1)
        }
    }
  }
</script>
